/* global React, ReactDOM, XLSX */
const { useState, useEffect, useCallback, useRef } = React;

const LOGO_B64 = "";

const G="#3d9b4e",GL="#e8f5ea",GM="#c3e6c8",G50="#f9fafb",G100="#f3f4f6",G200="#e5e7eb",G300="#d1d5db",G400="#9ca3af",G500="#6b7280",G700="#374151",G800="#1f2937",W="#ffffff",R="#ef4444",RL="#fef2f2",O="#f59e0b",OL="#fffbeb",B="#3b82f6",BL="#eff6ff";

function genId(){return Date.now().toString(36)+Math.random().toString(36).substr(2,5);}
function now(){return new Date().toLocaleString("fr-FR",{day:"2-digit",month:"2-digit",year:"numeric",hour:"2-digit",minute:"2-digit"});}
function today(){return new Date().toISOString().split("T")[0];}

const COLS=[
  {k:"reference",l:"Rfrence"},{k:"designation",l:"Dsignation"},
  {k:"dateEntree",l:"Date entre"},{k:"dateSortie",l:"Date sortie"},
  {k:"emplacement",l:"Emplacement"},{k:"quantite",l:"Quantit"},
  {k:"etat",l:"tat"},{k:"autresInfos",l:"Autres infos"},
];

const defaultState={clients:{},activeClient:null,users:[{id:"u1",name:"Admin",role:"admin",color:G}],activeUser:"u1",history:[]};

function App(){
  const [state,setState]=useState(defaultState);
  const [view,setView]=useState("home");
  const [editItem,setEditItem]=useState(null);
  const [formData,setFormData]=useState({});
  const [userFormData,setUserFormData]=useState({});
  const [editUser,setEditUser]=useState(null);
  const [search,setSearch]=useState("");
  const [filterStatus,setFilterStatus]=useState("all");
  const [toast,setToast]=useState(null);
  const [confirmDel,setConfirmDel]=useState(null);
  const [newClientName,setNewClientName]=useState("");
  const [showClientModal,setShowClientModal]=useState(false);
  const [sidebarOpen,setSidebarOpen]=useState(true);
  const [showImport,setShowImport]=useState(false);
  const [showExport,setShowExport]=useState(false);
  const [importData,setImportData]=useState(null);
  const [exportCols,setExportCols]=useState(COLS.map(c=>c.k));
  const [importMapping,setImportMapping]=useState({});
  const [loaded,setLoaded]=useState(false);
  const fileRef=useRef();

  useEffect(()=>{
    (async()=>{
      try{
        const res=await window.storage.get("mrdpstock_v3");
        if(res?.value)setState(JSON.parse(res.value));
      }catch(e){}
      setLoaded(true);
      document.getElementById('loading').style.display='none';
      document.getElementById('root').style.display='block';
    })();
  },[]);

  const save=useCallback(async(ns)=>{
    setState(ns);
    try{await window.storage.set("mrdpstock_v3",JSON.stringify(ns));}catch(e){}
  },[]);

  const toast_=(msg,type="success")=>{setToast({msg,type});setTimeout(()=>setToast(null),3200);};

  const addHistory=(action,detail,st)=>{
    const u=st.users.find(u=>u.id===st.activeUser);
    return [{id:genId(),ts:now(),user:u?.name||"Inconnu",action,detail},...(st.history||[])].slice(0,500);
  };

  const cl=state.activeClient&&state.clients[state.activeClient];
  const items=cl?.items||[];
  const activeUser=state.users.find(u=>u.id===state.activeUser);

  const filteredItems=items.filter(i=>{
    const s=search.toLowerCase();
    const match=!s||[i.reference,i.designation,i.emplacement,i.autresInfos].some(f=>f?.toLowerCase().includes(s));
    const st=filterStatus==="all"||(filterStatus==="in"&&i.etat==="en_stock")||(filterStatus==="out"&&i.etat==="sorti");
    return match&&st;
  });

  const statsTotal=items.length,statsIn=items.filter(i=>i.etat==="en_stock").length,statsOut=items.filter(i=>i.etat==="sorti").length;

  const createClient=()=>{
    if(!newClientName.trim())return;
    const id=genId();
    const ns={...state,clients:{...state.clients,[id]:{name:newClientName.trim(),items:[]}},activeClient:id};
    ns.history=addHistory("Client cr",`"${newClientName.trim()}"`,ns);
    save(ns);setNewClientName("");setShowClientModal(false);setView("stock");
    toast_(`Base "${newClientName.trim()}" cre`);
  };

  const openForm=(item=null)=>{
    if(item){setEditItem(item.id);setFormData({...item});}
    else{setEditItem(null);setFormData({reference:"",designation:"",dateEntree:today(),dateSortie:"",emplacement:"",quantite:"1",etat:"en_stock",autresInfos:""});}
    setView("form");
  };

  const saveItem=()=>{
    if(!formData.reference?.trim()||!formData.designation?.trim()){toast_("Rfrence et dsignation obligatoires","error");return;}
    let newItems,action,detail;
    if(editItem){newItems=items.map(i=>i.id===editItem?{...formData,id:editItem}:i);action="Article modifi";detail=`${formData.reference}  ${formData.designation}`;}
    else{newItems=[...items,{...formData,id:genId()}];action="Article ajout";detail=`${formData.reference}  ${formData.designation}`;}
    const ns={...state,clients:{...state.clients,[state.activeClient]:{...cl,items:newItems}}};
    ns.history=addHistory(action,detail,ns);
    save(ns);setView("stock");toast_(editItem?"Article modifi":"Article ajout");
  };

  const deleteItem=(id)=>{
    const item=items.find(i=>i.id===id);
    const ns={...state,clients:{...state.clients,[state.activeClient]:{...cl,items:items.filter(i=>i.id!==id)}}};
    ns.history=addHistory("Article supprim",`${item?.reference}  ${item?.designation}`,ns);
    save(ns);setConfirmDel(null);toast_("Article supprim","error");
  };

  const openUserForm=(user=null)=>{
    if(user){setEditUser(user.id);setUserFormData({...user});}
    else{setEditUser(null);setUserFormData({name:"",role:"user",color:["#3d9b4e","#3b82f6","#f59e0b","#ef4444","#8b5cf6"][Math.floor(Math.random()*5)]});}
    setView("userForm");
  };

  const saveUser=()=>{
    if(!userFormData.name?.trim()){toast_("Nom obligatoire","error");return;}
    const newUsers=editUser?state.users.map(u=>u.id===editUser?{...userFormData,id:editUser}:u):[...state.users,{...userFormData,id:genId()}];
    const ns={...state,users:newUsers};
    ns.history=addHistory(editUser?"Utilisateur modifi":"Utilisateur cr",userFormData.name,ns);
    save(ns);setView("users");toast_(editUser?"Utilisateur modifi":"Utilisateur cr");
  };

  const deleteUser=(id)=>{
    if(state.users.length<=1){toast_("Impossible: dernier utilisateur","error");setConfirmDel(null);return;}
    const u=state.users.find(u=>u.id===id);
    const newUsers=state.users.filter(u=>u.id!==id);
    const newActive=state.activeUser===id?newUsers[0].id:state.activeUser;
    const ns={...state,users:newUsers,activeUser:newActive};
    ns.history=addHistory("Utilisateur supprim",u?.name,ns);
    save(ns);setConfirmDel(null);toast_("Utilisateur supprim","error");
  };

  const switchUser=(id)=>{
    const u=state.users.find(u=>u.id===id);
    save({...state,activeUser:id,history:addHistory("Connexion",u?.name,{...state,activeUser:id})});
    toast_(`Connect: ${u?.name}`);
  };

  const handleFileChange=(e)=>{
    const file=e.target.files[0];
    if(!file)return;
    const reader=new FileReader();
    reader.onload=(ev)=>{
      try{
        const wb=XLSX.read(ev.target.result,{type:"binary",cellDates:true});
        const ws=wb.Sheets[wb.SheetNames[0]];
        const raw=XLSX.utils.sheet_to_json(ws,{header:1,defval:""});
        if(raw.length<2){toast_("Fichier vide","error");return;}
        const headers=raw[0].map(h=>String(h).trim());
        const rows=raw.slice(1).filter(r=>r.some(c=>c!=="")).map(r=>headers.reduce((o,h,i)=>({...o,[h]:r[i]!==undefined?String(r[i]).trim():""}),{}));
        const aliases={reference:["ref","rfrence","reference","code"],designation:["dsignation","designation","nom","libell","article"],dateEntree:["date entre","dateentree","entre","entree"],dateSortie:["date sortie","datesortie","sortie"],emplacement:["emplacement","lieu","localisation","zone"],quantite:["quantit","quantite","qt","qty","stock"],etat:["tat","etat","statut","status"],autresInfos:["autres","infos","note","notes","commentaire"]};
        const autoMap={};
        headers.forEach(h=>{const hl=h.toLowerCase().replace(/[^a-z0-9]/g,"");for(const[field,alts]of Object.entries(aliases)){if(alts.some(a=>hl.includes(a.replace(/[^a-z0-9]/g,"")))){if(!autoMap[field])autoMap[field]=h;}}});
        setImportData({rows,headers,preview:rows.slice(0,4)});setImportMapping(autoMap);setShowImport(true);
      }catch(err){toast_("Erreur: "+err.message,"error");}
    };
    reader.readAsBinaryString(file);e.target.value="";
  };

  const doImport=()=>{
    if(!importData)return;
    const newItems=importData.rows.map(row=>{
      const item={id:genId(),etat:"en_stock"};
      COLS.forEach(col=>{const mapped=importMapping[col.k];if(mapped&&row[mapped]!==undefined){let val=row[mapped];if(col.k==="etat"){const v=String(val).toLowerCase();val=(v.includes("sort")||v.includes("out")||v==="0"||v==="false")?"sorti":"en_stock";}item[col.k]=String(val);}else{item[col.k]="";}});
      return item;
    });
    const ns={...state,clients:{...state.clients,[state.activeClient]:{...cl,items:[...items,...newItems]}}};
    ns.history=addHistory("Import Excel",`${newItems.length} articles imports`,ns);
    save(ns);setShowImport(false);setImportData(null);toast_(`${newItems.length} articles imports`);
  };

  const doExport=()=>{
    const colsToExp=COLS.filter(c=>exportCols.includes(c.k));
    const header=colsToExp.map(c=>c.l);
    const rows=filteredItems.map(item=>colsToExp.map(c=>{if(c.k==="etat")return item.etat==="en_stock"?"En stock":"Sorti";return item[c.k]||"";}));
    const ws=XLSX.utils.aoa_to_sheet([header,...rows]);
    ws["!cols"]=colsToExp.map(()=>({wch:18}));
    const wb=XLSX.utils.book_new();XLSX.utils.book_append_sheet(wb,ws,cl?.name||"Stock");
    const fname=`MRDPSTOCK_${(cl?.name||"export").replace(/[^a-z0-9]/gi,"_")}_${new Date().toISOString().split("T")[0]}.xlsx`;
    XLSX.writeFile(wb,fname);setShowExport(false);toast_(`Export "${fname}" tlcharg`);
  };

  if(!loaded) return null;

  const S={
    app:{display:"flex",height:"100vh",fontFamily:"'Segoe UI','Inter',sans-serif",background:G50,color:G800,overflow:"hidden"},
    sidebar:{width:sidebarOpen?244:64,background:W,borderRight:`1px solid ${G200}`,display:"flex",flexDirection:"column",transition:"width .25s ease",overflow:"hidden",flexShrink:0},
    logo:{padding:"18px 14px",borderBottom:`1px solid ${G200}`,display:"flex",alignItems:"center",gap:10,cursor:"pointer",minHeight:68},
    logoImg:{width:36,height:36,borderRadius:8,flexShrink:0,objectFit:"cover"},
    logoText:{fontWeight:700,fontSize:14,color:G800,whiteSpace:"nowrap"},
    logoSub:{fontSize:9,color:G400,letterSpacing:1.5,textTransform:"uppercase"},
    navSection:{padding:"10px 8px 0",flex:1,overflowY:"auto"},
    navLabel:{fontSize:9,color:G400,textTransform:"uppercase",letterSpacing:1.5,padding:"0 8px 6px",whiteSpace:"nowrap"},
    navBtn:(a)=>({display:"flex",alignItems:"center",gap:10,padding:"8px 10px",borderRadius:8,cursor:"pointer",border:"none",background:a?GL:W,color:a?G:G500,width:"100%",textAlign:"left",fontSize:13,fontWeight:a?600:400,transition:"all .15s",marginBottom:2,whiteSpace:"nowrap"}),
    clientPill:(a)=>({display:"flex",alignItems:"center",gap:6,padding:"6px 10px",borderRadius:6,cursor:"pointer",border:"none",background:a?GL:"transparent",color:a?G:G500,width:"100%",textAlign:"left",fontSize:12,transition:"all .15s",marginBottom:1,overflow:"hidden"}),
    main:{flex:1,display:"flex",flexDirection:"column",overflow:"hidden"},
    topbar:{background:W,borderBottom:`1px solid ${G200}`,padding:"0 22px",height:58,display:"flex",alignItems:"center",justifyContent:"space-between",flexShrink:0,gap:12},
    content:{flex:1,overflowY:"auto",padding:22},
    card:{background:W,border:`1px solid ${G200}`,borderRadius:12,overflow:"hidden"},
    statCard:{background:W,border:`1px solid ${G200}`,borderRadius:12,padding:"18px 22px"},
    btn:(v="primary")=>({cursor:"pointer",border:"none",fontFamily:"inherit",borderRadius:8,padding:"8px 14px",fontSize:12,fontWeight:500,transition:"all .15s",display:"inline-flex",alignItems:"center",gap:6,...(v==="primary"?{background:G,color:W}:v==="ghost"?{background:"transparent",color:G500,border:`1px solid ${G200}`}:v==="danger"?{background:RL,color:R,border:`1px solid ${R}`}:{background:G100,color:G700,border:`1px solid ${G200}`})}),
    input:{width:"100%",padding:"8px 12px",borderRadius:8,border:`1px solid ${G200}`,background:W,color:G800,fontSize:13,fontFamily:"inherit",outline:"none",boxSizing:"border-box"},
    label:{display:"block",fontSize:11,fontWeight:600,color:G500,textTransform:"uppercase",letterSpacing:.8,marginBottom:5},
    th:{padding:"9px 14px",textAlign:"left",fontSize:11,fontWeight:600,color:G400,textTransform:"uppercase",letterSpacing:.8,whiteSpace:"nowrap",background:G50,borderBottom:`1px solid ${G200}`},
    td:{padding:"11px 14px",fontSize:13,color:G700,borderBottom:`1px solid ${G100}`},
    badge:(c)=>({padding:"3px 10px",borderRadius:20,fontSize:11,fontWeight:500,background:c==="green"?GL:c==="red"?RL:c==="orange"?OL:BL,color:c==="green"?G:c==="red"?R:c==="orange"?O:B}),
  };

  const Icon=({n,s=16})=>{
    const m={
      boxes:<svg width={s} height={s} fill="none" stroke="currentColor" strokeWidth={1.8} viewBox="0 0 24 24"><path d="M12 2L2 7l10 5 10-5-10-5zM2 17l10 5 10-5M2 12l10 5 10-5"/></svg>,
      history:<svg width={s} height={s} fill="none" stroke="currentColor" strokeWidth={1.8} viewBox="0 0 24 24"><circle cx="12" cy="12" r="10"/><polyline points="12 6 12 12 16 14"/></svg>,
      users:<svg width={s} height={s} fill="none" stroke="currentColor" strokeWidth={1.8} viewBox="0 0 24 24"><path d="M17 21v-2a4 4 0 0 0-4-4H5a4 4 0 0 0-4 4v2"/><circle cx="9" cy="7" r="4"/><path d="M23 21v-2a4 4 0 0 0-3-3.87M16 3.13a4 4 0 0 1 0 7.75"/></svg>,
      plus:<svg width={s} height={s} fill="none" stroke="currentColor" strokeWidth={2} viewBox="0 0 24 24"><line x1="12" y1="5" x2="12" y2="19"/><line x1="5" y1="12" x2="19" y2="12"/></svg>,
      edit:<svg width={s} height={s} fill="none" stroke="currentColor" strokeWidth={1.8} viewBox="0 0 24 24"><path d="M11 4H4a2 2 0 0 0-2 2v14a2 2 0 0 0 2 2h14a2 2 0 0 0 2-2v-7"/><path d="M18.5 2.5a2.121 2.121 0 0 1 3 3L12 15l-4 1 1-4 9.5-9.5z"/></svg>,
      trash:<svg width={s} height={s} fill="none" stroke="currentColor" strokeWidth={1.8} viewBox="0 0 24 24"><polyline points="3 6 5 6 21 6"/><path d="M19 6v14a2 2 0 0 1-2 2H7a2 2 0 0 1-2-2V6m3 0V4a1 1 0 0 1 1-1h4a1 1 0 0 1 1 1v2"/></svg>,
      search:<svg width={s} height={s} fill="none" stroke="currentColor" strokeWidth={1.8} viewBox="0 0 24 24"><circle cx="11" cy="11" r="8"/><line x1="21" y1="21" x2="16.65" y2="16.65"/></svg>,
      upload:<svg width={s} height={s} fill="none" stroke="currentColor" strokeWidth={1.8} viewBox="0 0 24 24"><path d="M21 15v4a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2v-4"/><polyline points="17 8 12 3 7 8"/><line x1="12" y1="3" x2="12" y2="15"/></svg>,
      download:<svg width={s} height={s} fill="none" stroke="currentColor" strokeWidth={1.8} viewBox="0 0 24 24"><path d="M21 15v4a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2v-4"/><polyline points="7 10 12 15 17 10"/><line x1="12" y1="15" x2="12" y2="3"/></svg>,
      arrow:<svg width={s} height={s} fill="none" stroke="currentColor" strokeWidth={2} viewBox="0 0 24 24"><line x1="19" y1="12" x2="5" y2="12"/><polyline points="12 19 5 12 12 5"/></svg>,
      home:<svg width={s} height={s} fill="none" stroke="currentColor" strokeWidth={1.8} viewBox="0 0 24 24"><path d="M3 9l9-7 9 7v11a2 2 0 0 1-2 2H5a2 2 0 0 1-2-2z"/><polyline points="9 22 9 12 15 12 15 22"/></svg>,
      check:<svg width={s} height={s} fill="none" stroke="currentColor" strokeWidth={2.5} viewBox="0 0 24 24"><polyline points="20 6 9 17 4 12"/></svg>,
    };
    return m[n]||null;
  };

  const Modal=({title,children,onClose,footer,wide})=>(
    <div style={{position:"fixed",inset:0,background:"rgba(0,0,0,.42)",display:"flex",alignItems:"center",justifyContent:"center",zIndex:1000,padding:20}}>
      <div style={{background:W,borderRadius:16,width:wide?680:440,maxWidth:"100%",maxHeight:"90vh",display:"flex",flexDirection:"column",boxShadow:"0 24px 64px rgba(0,0,0,.18)"}}>
        <div style={{padding:"18px 22px 14px",borderBottom:`1px solid ${G100}`,display:"flex",justifyContent:"space-between",alignItems:"center",flexShrink:0}}>
          <span style={{fontWeight:600,fontSize:15}}>{title}</span>
          <button onClick={onClose} style={{background:"none",border:"none",cursor:"pointer",color:G400,padding:4,fontSize:16}}></button>
        </div>
        <div style={{padding:22,overflowY:"auto",flex:1}}>{children}</div>
        {footer&&<div style={{padding:"14px 22px",borderTop:`1px solid ${G100}`,display:"flex",gap:8,justifyContent:"flex-end",flexShrink:0}}>{footer}</div>}
      </div>
    </div>
  );

  return (
    <div style={S.app}>
      <style>{`
        *{box-sizing:border-box;margin:0;padding:0}
        ::-webkit-scrollbar{width:5px;height:5px}::-webkit-scrollbar-track{background:${G100}}::-webkit-scrollbar-thumb{background:${G300};border-radius:3px}
        button:active{transform:scale(.98)}
        .row:hover td{background:${G50}!important}
        .nb:hover{background:${G50}!important;color:${G800}!important}
        @keyframes fadeUp{from{opacity:0;transform:translateY(6px)}to{opacity:1;transform:translateY(0)}}
        @keyframes toastIn{from{opacity:0;transform:translateX(20px)}to{opacity:1;transform:translateX(0)}}
        .anim{animation:fadeUp .2s ease}
        input:focus,select:focus,textarea:focus{border-color:${G}!important;box-shadow:0 0 0 3px ${GL}}
        .chk{display:flex;align-items:center;gap:8px;cursor:pointer;padding:8px 10px;border-radius:8px;transition:background .1s}
        .chk:hover{background:${G50}}
      `}</style>

      <input ref={fileRef} type="file" accept=".xlsx,.xls,.csv" style={{display:"none"}} onChange={handleFileChange}/>

      {toast&&<div style={{position:"fixed",top:18,right:18,zIndex:9999,padding:"11px 18px",borderRadius:10,background:toast.type==="error"?R:G,color:W,fontSize:13,fontWeight:500,boxShadow:"0 8px 24px rgba(0,0,0,.15)",animation:"toastIn .2s ease"}}>{toast.msg}</div>}

      {confirmDel&&(
        <Modal title="Confirmer la suppression" onClose={()=>setConfirmDel(null)} footer={<>
          <button style={S.btn("secondary")} onClick={()=>setConfirmDel(null)}>Annuler</button>
          <button style={S.btn("danger")} onClick={()=>{if(confirmDel.type==="item")deleteItem(confirmDel.id);else deleteUser(confirmDel.id);}}>Supprimer</button>
        </>}>
          <p style={{color:G500,fontSize:13}}>Supprimer <strong>"{confirmDel.label}"</strong> ? Cette action est irrversible.</p>
        </Modal>
      )}

      {showClientModal&&(
        <Modal title="Nouveau client" onClose={()=>setShowClientModal(false)} footer={<>
          <button style={S.btn("secondary")} onClick={()=>setShowClientModal(false)}>Annuler</button>
          <button style={S.btn()} onClick={createClient}>Crer</button>
        </>}>
          <label style={S.label}>Nom du client</label>
          <input style={S.input} value={newClientName} onChange={e=>setNewClientName(e.target.value)} onKeyDown={e=>e.key==="Enter"&&createClient()} placeholder="Ex: Entreprise Dupont" autoFocus/>
        </Modal>
      )}

      {showImport&&importData&&(
        <Modal title={`Importer Excel  ${importData.rows.length} lignes`} wide onClose={()=>{setShowImport(false);setImportData(null);}} footer={<>
          <span style={{color:G400,fontSize:12,flex:1}}>Vrifiez le mapping avant d'importer</span>
          <button style={S.btn("secondary")} onClick={()=>{setShowImport(false);setImportData(null);}}>Annuler</button>
          <button style={S.btn()} onClick={doImport}><Icon n="upload" s={14}/>Importer {importData.rows.length} articles</button>
        </>}>
          <p style={{fontSize:12,color:G500,marginBottom:16}}>Associez les colonnes de votre fichier aux champs MRDPSTOCK.</p>
          <div style={{display:"grid",gridTemplateColumns:"1fr 1fr",gap:10,marginBottom:20}}>
            {COLS.map(col=>(
              <div key={col.k}>
                <label style={S.label}>{col.l}</label>
                <select style={S.input} value={importMapping[col.k]||""} onChange={e=>setImportMapping({...importMapping,[col.k]:e.target.value||undefined})}>
                  <option value=""> Ne pas importer </option>
                  {importData.headers.map(h=><option key={h} value={h}>{h}</option>)}
                </select>
              </div>
            ))}
          </div>
          <div style={{background:G50,borderRadius:10,overflow:"hidden",border:`1px solid ${G200}`}}>
            <div style={{padding:"7px 12px",fontSize:11,fontWeight:600,color:G400,textTransform:"uppercase",letterSpacing:.8}}>Aperu</div>
            <div style={{overflowX:"auto"}}>
              <table style={{width:"100%",borderCollapse:"collapse",fontSize:11}}>
                <thead><tr>{importData.headers.map(h=><th key={h} style={{...S.th,padding:"6px 10px",fontSize:10}}>{h}</th>)}</tr></thead>
                <tbody>{importData.preview.map((row,i)=>(
                  <tr key={i}>{importData.headers.map(h=><td key={h} style={{...S.td,padding:"5px 10px",fontSize:11}}>{row[h]||""}</td>)}</tr>
                ))}</tbody>
              </table>
            </div>
          </div>
        </Modal>
      )}

      {showExport&&(
        <Modal title="Exporter en Excel" onClose={()=>setShowExport(false)} footer={<>
          <button style={S.btn("secondary")} onClick={()=>setShowExport(false)}>Annuler</button>
          <button style={S.btn()} onClick={doExport}><Icon n="download" s={14}/>Tlcharger .xlsx</button>
        </>}>
          <p style={{fontSize:12,color:G500,marginBottom:16}}>Colonnes  exporter ({filteredItems.length} articles).</p>
          <div style={{display:"flex",justifyContent:"flex-end",gap:8,marginBottom:12}}>
            <button style={{...S.btn("ghost"),padding:"5px 10px",fontSize:11}} onClick={()=>setExportCols(COLS.map(c=>c.k))}>Tout</button>
            <button style={{...S.btn("ghost"),padding:"5px 10px",fontSize:11}} onClick={()=>setExportCols([])}>Aucun</button>
          </div>
          <div style={{display:"grid",gridTemplateColumns:"1fr 1fr",gap:4}}>
            {COLS.map(col=>{
              const checked=exportCols.includes(col.k);
              return(
                <label key={col.k} className="chk" style={{background:checked?GL:W,border:`1px solid ${checked?GM:G200}`,borderRadius:8}} onClick={()=>setExportCols(p=>p.includes(col.k)?p.filter(c=>c!==col.k):[...p,col.k])}>
                  <div style={{width:18,height:18,borderRadius:5,background:checked?G:W,border:`2px solid ${checked?G:G300}`,display:"flex",alignItems:"center",justifyContent:"center",flexShrink:0}}>
                    {checked&&<Icon n="check" s={10}/>}
                  </div>
                  <span style={{fontSize:13,fontWeight:checked?500:400,color:checked?G:G700}}>{col.l}</span>
                </label>
              );
            })}
          </div>
        </Modal>
      )}

      {/* SIDEBAR */}
      <div style={S.sidebar}>
        <div style={S.logo} onClick={()=>setSidebarOpen(!sidebarOpen)}>
          <img src={`data:image/png;base64,${LOGO_B64}`} alt="Logo" style={S.logoImg}/>
          {sidebarOpen&&<div><div style={S.logoText}>MRDPSTOCK</div><div style={S.logoSub}>M.R.D.P.S 27</div></div>}
        </div>
        <div style={S.navSection}>
          {sidebarOpen&&<div style={S.navLabel}>Navigation</div>}
          {[{id:"home",icon:"home",label:"Tableau de bord"},{id:"history",icon:"history",label:"Historique"},{id:"users",icon:"users",label:"Utilisateurs"}].map(nav=>(
            <button key={nav.id} className="nb" style={S.navBtn(view===nav.id)} onClick={()=>{setView(nav.id);setState(p=>({...p,activeClient:null}));}}>
              <Icon n={nav.icon} s={16}/>{sidebarOpen&&<span>{nav.label}</span>}
            </button>
          ))}
          {sidebarOpen&&Object.keys(state.clients).length>0&&<>
            <div style={{...S.navLabel,marginTop:14}}>Bases clients</div>
            {Object.entries(state.clients).map(([id,c])=>(
              <button key={id} className="nb" style={S.clientPill(state.activeClient===id&&view==="stock")}
                onClick={()=>{save({...state,activeClient:id});setView("stock");setSearch("");setFilterStatus("all");}}>
                <div style={{width:7,height:7,borderRadius:"50%",background:G,flexShrink:0}}/>
                <span style={{overflow:"hidden",textOverflow:"ellipsis",whiteSpace:"nowrap",flex:1}}>{c.name}</span>
                <span style={{color:G400,fontSize:10}}>{c.items.length}</span>
              </button>
            ))}
          </>}
          <button className="nb" style={{...S.navBtn(false),marginTop:8,borderTop:`1px solid ${G100}`,paddingTop:12}} onClick={()=>setShowClientModal(true)}>
            <Icon n="plus" s={16}/>{sidebarOpen&&<span>Nouveau client</span>}
          </button>
        </div>
        {sidebarOpen&&<div style={{padding:"12px 14px",borderTop:`1px solid ${G200}`}}>
          <div style={{display:"flex",alignItems:"center",gap:9}}>
            <div style={{width:30,height:30,borderRadius:"50%",background:activeUser?.color||G,color:W,display:"flex",alignItems:"center",justifyContent:"center",fontSize:12,fontWeight:700,flexShrink:0}}>{activeUser?.name?.[0]?.toUpperCase()||"?"}</div>
            <div><div style={{fontSize:12,fontWeight:600}}>{activeUser?.name||"Utilisateur"}</div><div style={{fontSize:10,color:G400,textTransform:"capitalize"}}>{activeUser?.role||"user"}</div></div>
          </div>
        </div>}
      </div>

      {/* MAIN */}
      <div style={S.main}>
        <div style={S.topbar}>
          <div style={{display:"flex",alignItems:"center",gap:10,overflow:"hidden"}}>
            {view==="stock"&&cl&&<><div style={{fontWeight:600,fontSize:15,whiteSpace:"nowrap"}}>{cl.name}</div><span style={{color:G300}}>|</span><span style={{fontSize:12,color:G400}}>{items.length} article{items.length!==1?"s":""}</span></>}
            {view==="home"&&<div style={{fontWeight:600,fontSize:15}}>Tableau de bord</div>}
            {view==="history"&&<div style={{fontWeight:600,fontSize:15}}>Historique</div>}
            {view==="users"&&<div style={{fontWeight:600,fontSize:15}}>Utilisateurs</div>}
            {view==="form"&&<div style={{fontWeight:600,fontSize:15}}>{editItem?"Modifier":"Nouvel article"}</div>}
            {view==="userForm"&&<div style={{fontWeight:600,fontSize:15}}>{editUser?"Modifier":"Nouvel utilisateur"}</div>}
          </div>
          <div style={{display:"flex",gap:8,flexShrink:0}}>
            {view==="stock"&&cl&&<>
              <button style={S.btn("secondary")} onClick={()=>fileRef.current.click()}><Icon n="upload" s={14}/>Import Excel</button>
              <button style={S.btn("secondary")} onClick={()=>{setExportCols(COLS.map(c=>c.k));setShowExport(true);}}><Icon n="download" s={14}/>Exporter</button>
              <button style={S.btn()} onClick={()=>openForm()}><Icon n="plus" s={14}/>Ajouter</button>
            </>}
            {view==="users"&&<button style={S.btn()} onClick={()=>openUserForm()}><Icon n="plus" s={14}/>Ajouter</button>}
          </div>
        </div>

        <div style={S.content}>

          {view==="home"&&<div className="anim">
            <div style={{display:"grid",gridTemplateColumns:"repeat(auto-fill,minmax(170px,1fr))",gap:14,marginBottom:22}}>
              {[{l:"Bases clients",v:Object.keys(state.clients).length,c:B},{l:"Articles total",v:Object.values(state.clients).reduce((s,c)=>s+c.items.length,0),c:G},{l:"En stock",v:Object.values(state.clients).reduce((s,c)=>s+c.items.filter(i=>i.etat==="en_stock").length,0),c:G},{l:"Sortis",v:Object.values(state.clients).reduce((s,c)=>s+c.items.filter(i=>i.etat==="sorti").length,0),c:R},{l:"Utilisateurs",v:state.users.length,c:O}].map(s=>(
                <div key={s.l} style={S.statCard}><div style={{fontSize:26,fontWeight:700,color:s.c,marginBottom:3}}>{s.v}</div><div style={{fontSize:12,color:G400}}>{s.l}</div></div>
              ))}
            </div>
            {Object.keys(state.clients).length===0?(
              <div style={{...S.card,padding:48,textAlign:"center"}}>
                <div style={{fontSize:40,marginBottom:12}}></div>
                <div style={{fontWeight:600,fontSize:15,marginBottom:8}}>Aucun client</div>
                <p style={{color:G400,fontSize:13,marginBottom:20}}>Crez votre premire base client pour dmarrer.</p>
                <button style={S.btn()} onClick={()=>setShowClientModal(true)}>Crer ma premire base client</button>
              </div>
            ):(
              <div style={S.card}>
                <div style={{padding:"14px 18px",borderBottom:`1px solid ${G100}`,fontWeight:600,fontSize:13}}>Bases clients</div>
                <table style={{width:"100%",borderCollapse:"collapse"}}>
                  <thead><tr>{["Client","Articles","En stock","Sortis",""].map(h=><th key={h} style={S.th}>{h}</th>)}</tr></thead>
                  <tbody>{Object.entries(state.clients).map(([id,c])=>(
                    <tr key={id} className="row">
                      <td style={S.td}><div style={{fontWeight:500}}>{c.name}</div></td>
                      <td style={S.td}>{c.items.length}</td>
                      <td style={S.td}><span style={S.badge("green")}>{c.items.filter(i=>i.etat==="en_stock").length}</span></td>
                      <td style={S.td}><span style={S.badge("red")}>{c.items.filter(i=>i.etat==="sorti").length}</span></td>
                      <td style={S.td}><button style={{...S.btn("ghost"),padding:"5px 12px",fontSize:12}} onClick={()=>{save({...state,activeClient:id});setView("stock");}}>Ouvrir</button></td>
                    </tr>
                  ))}</tbody>
                </table>
              </div>
            )}
          </div>}

          {view==="stock"&&cl&&<div className="anim">
            <div style={{display:"grid",gridTemplateColumns:"repeat(3,1fr)",gap:14,marginBottom:20}}>
              {[{l:"Total",v:statsTotal,c:B},{l:"En stock",v:statsIn,c:G},{l:"Sortis",v:statsOut,c:R}].map(s=>(
                <div key={s.l} style={S.statCard}><div style={{fontSize:24,fontWeight:700,color:s.c}}>{s.v}</div><div style={{fontSize:12,color:G400}}>{s.l}</div></div>
              ))}
            </div>
            <div style={{display:"flex",gap:8,marginBottom:14,alignItems:"center"}}>
              <div style={{position:"relative",flex:1}}>
                <span style={{position:"absolute",left:11,top:"50%",transform:"translateY(-50%)",color:G400}}><Icon n="search" s={15}/></span>
                <input style={{...S.input,paddingLeft:34}} placeholder="Rechercher..." value={search} onChange={e=>setSearch(e.target.value)}/>
              </div>
              {["all","in","out"].map(f=>(
                <button key={f} style={{...S.btn(filterStatus===f?"primary":"secondary"),padding:"8px 12px"}} onClick={()=>setFilterStatus(f)}>
                  {f==="all"?"Tout":f==="in"?"En stock":"Sortis"}
                </button>
              ))}
            </div>
            <div style={S.card}>
              <div style={{overflowX:"auto"}}>
                <table style={{width:"100%",borderCollapse:"collapse",minWidth:860}}>
                  <thead><tr>{["Rfrence","Dsignation","Date entre","Date sortie","Emplacement","Qt","tat","Autres infos",""].map(h=><th key={h} style={S.th}>{h}</th>)}</tr></thead>
                  <tbody>
                    {filteredItems.length===0&&<tr><td colSpan={9} style={{...S.td,textAlign:"center",color:G400,padding:40}}>{items.length===0?"Aucun article  ajoutez-en ou importez un fichier Excel":"Aucun rsultat"}</td></tr>}
                    {filteredItems.map(item=>(
                      <tr key={item.id} className="row">
                        <td style={{...S.td,fontWeight:600,color:G}}>{item.reference}</td>
                        <td style={S.td}>{item.designation}</td>
                        <td style={{...S.td,color:G400,fontSize:12}}>{item.dateEntree||""}</td>
                        <td style={{...S.td,color:G400,fontSize:12}}>{item.dateSortie||""}</td>
                        <td style={S.td}>{item.emplacement||""}</td>
                        <td style={S.td}>{item.quantite||""}</td>
                        <td style={S.td}><span style={S.badge(item.etat==="en_stock"?"green":"red")}>{item.etat==="en_stock"?"En stock":"Sorti"}</span></td>
                        <td style={{...S.td,maxWidth:160,overflow:"hidden",textOverflow:"ellipsis",whiteSpace:"nowrap",color:G400,fontSize:12}}>{item.autresInfos||""}</td>
                        <td style={{...S.td,whiteSpace:"nowrap"}}>
                          <button style={{background:"none",border:"none",cursor:"pointer",color:B,padding:"4px 6px",borderRadius:6}} onClick={()=>openForm(item)}><Icon n="edit" s={14}/></button>
                          <button style={{background:"none",border:"none",cursor:"pointer",color:R,padding:"4px 6px",borderRadius:6}} onClick={()=>setConfirmDel({type:"item",id:item.id,label:item.designation})}><Icon n="trash" s={14}/></button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          </div>}

          {view==="form"&&<div className="anim" style={{maxWidth:660}}>
            <button style={{...S.btn("ghost"),marginBottom:18,fontSize:12}} onClick={()=>setView("stock")}><Icon n="arrow" s={14}/>Retour</button>
            <div style={{...S.card,padding:26}}>
              <h2 style={{fontSize:16,fontWeight:600,marginBottom:22}}>{editItem?"Modifier l'article":"Nouvel article"}</h2>
              <div style={{display:"grid",gridTemplateColumns:"1fr 1fr",gap:14}}>
                {[{k:"reference",l:"Rfrence *",t:"text",p:"REF-001"},{k:"designation",l:"Dsignation *",t:"text",p:"Nom de l'article"},{k:"dateEntree",l:"Date d'entre",t:"date"},{k:"dateSortie",l:"Date de sortie",t:"date"},{k:"emplacement",l:"Emplacement",t:"text",p:"A1, Rayon 3..."},{k:"quantite",l:"Quantit",t:"text",p:"1"}].map(f=>(
                  <div key={f.k}><label style={S.label}>{f.l}</label><input type={f.t} style={S.input} value={formData[f.k]||""} onChange={e=>setFormData({...formData,[f.k]:e.target.value})} placeholder={f.p||""}/></div>
                ))}
                <div><label style={S.label}>tat</label>
                  <select style={S.input} value={formData.etat||"en_stock"} onChange={e=>setFormData({...formData,etat:e.target.value})}>
                    <option value="en_stock">En stock</option><option value="sorti">Sorti</option>
                  </select>
                </div>
                <div style={{gridColumn:"1/-1"}}><label style={S.label}>Autres informations</label>
                  <textarea style={{...S.input,resize:"vertical",minHeight:68}} value={formData.autresInfos||""} onChange={e=>setFormData({...formData,autresInfos:e.target.value})} placeholder="Notes, numro de srie..."/>
                </div>
              </div>
              <div style={{display:"flex",gap:8,marginTop:20,justifyContent:"flex-end"}}>
                <button style={S.btn("ghost")} onClick={()=>setView("stock")}>Annuler</button>
                <button style={S.btn()} onClick={saveItem}>{editItem?"Enregistrer":"Ajouter"}</button>
              </div>
            </div>
          </div>}

          {view==="history"&&<div className="anim">
            <div style={S.card}>
              {state.history.length===0?<div style={{padding:48,textAlign:"center",color:G400}}>Aucune action enregistre.</div>:(
                <table style={{width:"100%",borderCollapse:"collapse"}}>
                  <thead><tr>{["Date / Heure","Utilisateur","Action","Dtail"].map(h=><th key={h} style={S.th}>{h}</th>)}</tr></thead>
                  <tbody>{state.history.map(h=>(
                    <tr key={h.id} className="row">
                      <td style={{...S.td,fontSize:12,color:G400,whiteSpace:"nowrap"}}>{h.ts}</td>
                      <td style={S.td}><div style={{display:"flex",alignItems:"center",gap:7}}><div style={{width:24,height:24,borderRadius:"50%",background:state.users.find(u=>u.name===h.user)?.color||G300,color:W,display:"flex",alignItems:"center",justifyContent:"center",fontSize:10,fontWeight:700,flexShrink:0}}>{h.user?.[0]?.toUpperCase()}</div><span style={{fontSize:12,fontWeight:500}}>{h.user}</span></div></td>
                      <td style={S.td}><span style={S.badge(h.action.includes("supprim")?"red":h.action.includes("cr")||h.action.includes("ajout")||h.action.includes("mport")?"green":"blue")}>{h.action}</span></td>
                      <td style={{...S.td,color:G500,fontSize:12}}>{h.detail}</td>
                    </tr>
                  ))}</tbody>
                </table>
              )}
            </div>
          </div>}

          {view==="users"&&<div className="anim">
            <div style={S.card}>
              <table style={{width:"100%",borderCollapse:"collapse"}}>
                <thead><tr>{["Utilisateur","Rle","Statut","Actions"].map(h=><th key={h} style={S.th}>{h}</th>)}</tr></thead>
                <tbody>{state.users.map(u=>(
                  <tr key={u.id} className="row">
                    <td style={S.td}><div style={{display:"flex",alignItems:"center",gap:9}}><div style={{width:34,height:34,borderRadius:"50%",background:u.color,color:W,display:"flex",alignItems:"center",justifyContent:"center",fontSize:13,fontWeight:700}}>{u.name[0].toUpperCase()}</div><div><div style={{fontWeight:500}}>{u.name}</div><div style={{fontSize:11,color:G400}}>ID: {u.id}</div></div></div></td>
                    <td style={S.td}><span style={S.badge(u.role==="admin"?"orange":"blue")}>{u.role==="admin"?"Administrateur":"Utilisateur"}</span></td>
                    <td style={S.td}>{state.activeUser===u.id?<span style={S.badge("green")}> Connect</span>:<button style={{...S.btn("ghost"),padding:"5px 12px",fontSize:11}} onClick={()=>switchUser(u.id)}>Connecter</button>}</td>
                    <td style={{...S.td,whiteSpace:"nowrap"}}>
                      <button style={{background:"none",border:"none",cursor:"pointer",color:B,padding:"4px 6px",borderRadius:6}} onClick={()=>openUserForm(u)}><Icon n="edit" s={14}/></button>
                      {state.users.length>1&&<button style={{background:"none",border:"none",cursor:"pointer",color:R,padding:"4px 6px",borderRadius:6}} onClick={()=>setConfirmDel({type:"user",id:u.id,label:u.name})}><Icon n="trash" s={14}/></button>}
                    </td>
                  </tr>
                ))}</tbody>
              </table>
            </div>
          </div>}

          {view==="userForm"&&<div className="anim" style={{maxWidth:460}}>
            <button style={{...S.btn("ghost"),marginBottom:18,fontSize:12}} onClick={()=>setView("users")}><Icon n="arrow" s={14}/>Retour</button>
            <div style={{...S.card,padding:26}}>
              <h2 style={{fontSize:16,fontWeight:600,marginBottom:22}}>{editUser?"Modifier":"Nouvel utilisateur"}</h2>
              <div style={{display:"grid",gap:14}}>
                <div><label style={S.label}>Nom *</label><input style={S.input} value={userFormData.name||""} onChange={e=>setUserFormData({...userFormData,name:e.target.value})} placeholder="Prnom Nom" autoFocus/></div>
                <div><label style={S.label}>Rle</label>
                  <select style={S.input} value={userFormData.role||"user"} onChange={e=>setUserFormData({...userFormData,role:e.target.value})}>
                    <option value="user">Utilisateur</option><option value="admin">Administrateur</option>
                  </select>
                </div>
                <div><label style={S.label}>Couleur avatar</label>
                  <div style={{display:"flex",gap:8,flexWrap:"wrap"}}>
                    {["#3d9b4e","#3b82f6","#f59e0b","#ef4444","#8b5cf6","#ec4899","#14b8a6","#f97316"].map(c=>(
                      <div key={c} onClick={()=>setUserFormData({...userFormData,color:c})} style={{width:30,height:30,borderRadius:"50%",background:c,cursor:"pointer",border:`3px solid ${userFormData.color===c?"#1f2937":"transparent"}`,transition:"border .12s"}}/>
                    ))}
                  </div>
                </div>
              </div>
              <div style={{display:"flex",gap:8,marginTop:22,justifyContent:"flex-end"}}>
                <button style={S.btn("ghost")} onClick={()=>setView("users")}>Annuler</button>
                <button style={S.btn()} onClick={saveUser}>{editUser?"Enregistrer":"Crer"}</button>
              </div>
            </div>
          </div>}

        </div>
      </div>
    </div>
  );
}

const container = document.getElementById('root');
const reactRoot = ReactDOM.createRoot(container);
reactRoot.render(<App/>);
